mapper.select:
SELECT * FROM (SELECT result1.*, ROWNUM AS rnum FROM (SELECT num,writer,title,content,viewCount,regdate FROM qna
ORDER BY num DESC) result1)
WHERE rnum >= #{qnaStartRowNum} AND rnum <= #{qnaEndRowNum}

mapper.update:
UPDATE qna
SET viewCount = viewCount+1
WHERE num=#{num}

UPDATE qna
SET title=#{title}, content='${content}'
WHERE num=#{num}
