mapper.select:
SELECT * FROM (SELECT result1.*, ROWNUM AS rnum FROM (SELECT num,writer,title,content,viewCount,TO_CHAR(regdate, 'YY.MM.DD HH24:MI') AS regdate FROM qna
ORDER BY num DESC) result1)
WHERE rnum >= #{qnaStartRowNum} AND rnum <= #{qnaEndRowNum}

SELECT users_num
FROM space_users
WHERE id=#{id}

mapper.update:
UPDATE qna
SET viewCount = viewCount+1
WHERE num=#{num}

UPDATE qna
SET title=#{title}, content='${content}'
WHERE num=#{num}
