mapper.select:
SELECT * FROM (SELECT result1.*, ROWNUM AS rnum FROM (SELECT review_num,review_writer,review_title,review_content,viewcount,review_regdate FROM review
ORDER BY review_num DESC) result1)
WHERE rnum >= #{startRowNum} AND rnum <= #{endRowNum}

mapper.update:
UPDATE review
SET viewcount = viewcount+1
WHERE review_num=#{review_num}

UPDATE review
SET review_title=#{review_title}, review_content='${review_content}'
WHERE review_num=#{review_num}
