mapper.select:
SELECT * FROM ( SELECT review_num, review_writer, review_title, review_content, viewcount, TO_CHAR(review_regdate, 'YY.MM.DD HH24:MI') AS review_regdate, star, ROW_NUMBER() OVER (ORDER BY review_num) AS rnum FROM review INNER JOIN seller_space ON review.space_num = seller_space.space_num INNER JOIN space_users ON seller_space.users_num = space_users.users_num WHERE space_users.id = #{sellerId} ) sub
WHERE rnum BETWEEN #{startRowNum} AND #{endRowNum}
ORDER BY review_num

SELECT users_num
FROM space_users
WHERE id=#{id}

SELECT NVL(MAX(ROWNUM), 0)
FROM review
WHERE space_num = #{space_num}

SELECT NVL(MAX(ROWNUM), 0)
FROM review
WHERE users_num=#{users_num}

mapper.update:
UPDATE review
SET viewcount = viewcount+1
WHERE review_num=#{review_num}

UPDATE review
SET review_title=#{review_title}, review_content='${review_content}', star=#{star}
WHERE review_num=#{review_num}

mapper.select:
SELECT * FROM (SELECT * FROM seller_space INNER JOIN space_category ON seller_space.cate_name = space_category.cate_name ) result1 INNER JOIN reservationform ON reservationform.space_num=result1.space_num WHERE reservationform.users_id=#{review_writer} AND reservationform.ispaid = 'true'
AND (reserv_date< TO_CHAR(SYSDATE, 'YYYY-MM-DD'))
#{item}

SELECT reserv_num
FROM review
WHERE review_writer = #{review_writer}
