mapper.select:
SELECT space_name, oneliner, intro, imagePath, addr
FROM seller_space
WHERE space_num=#{space_num}

mapper.update:
UPDATE seller_space
SET space_name=#{space_name}, oneliner=#{oneliner}, intro=#{intro},
	imagePath=#{imagePath}, addr=#{addr}, cate_name=#{cate_name}
WHERE space_num=#{space_num}
