mapper.select:
SELECT space_name, oneliner, intro, mainImagePath, addr
FROM seller_space
WHERE space_num=#{space_num}

mapper.update:
UPDATE seller_space
SET space_name=#{space_name}, oneliner=#{oneliner}, intro=#{intro},
	mainImagePath=#{mainImagePath}, addr=#{addr}, cate_name=#{cate_name}
WHERE space_num=#{space_num}
