mapper.select:
SELECT *
FROM
	(SELECT result1.*, ROWNUM AS rnum
	FROM
		(SELECT space_num, users_num, space_name, mainImagePath, addr, seller_space.cate_name cate_name, oneliner
		FROM seller_space
		INNER JOIN space_category ON seller_space.cate_name=space_category.cate_name
				WHERE space_category.cate_num = #{cate_num}


		               	#{item}


		) result1)
WHERE rnum BETWEEN #{startRowNum} AND #{endRowNum}
ORDER BY space_num DESC

		SELECT NVL(MAX(ROWNUM), 0)
		FROM seller_space
		JOIN space_category ON seller_space.cate_name=space_category.cate_name
		WHERE space_category.cate_num = #{cate_num}


               	#{item}

SELECT space_num, users_num, space_name, mainImagePath, addr, seller_space.cate_name cate_name, oneliner, intro
FROM seller_space
WHERE space_num=#{space_num}
